sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	// Modified the if to return if the newValue == oldValue to avoid
	// unecessary trips to the server
	//if (isLoading || newValue === '' || newValue == oldValue) {
	if (newValue === '' || newValue == oldValue) {
		return;
	}

	// Instantiate the GetEmailAddress Script Include 
	var checkRoles = new GlideAjax('ClientWayCheckRole');
	checkRoles.addParam('sysparm_name','isApvRole');
	checkRoles.addParam('sysparm_userID', newValue);
	//approval user need role of priviege
	checkRoles.addParam('sysparm_apvRole', 'x_1782169_ask_sh_0.priviege');

	checkRoles.getXMLAnswer(function(response){
		//g_form.addInfoMessage('response=' + response);

		if ( response != 'true' ){
			g_form.clearValue('request_for');
			g_form.addErrorMessage('Not priviege role, Please retry [Request for].');
		}   
	});
}
