sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	// Modified the if to return if the newValue == oldValue to avoid
	// unecessary trips to the server
	//if (isLoading || newValue === '' || newValue == oldValue) {
	if (newValue === '' || newValue == oldValue) {
		return;
	}

	// Instantiate the GetEmailAddress Script Include 
	var getEmailAddr = new GlideAjax('ClientWayCheckRole');
	getEmailAddr.addParam('sysparm_name','isApvRole');
	getEmailAddr.addParam('sysparm_userID', g_form.getValue('request_for'));
	//approval user need role of priviege
	//getEmailAddr.addParam('sysparm_apvRole', 'x_1782169_ask_sh_0.priviege');
	getEmailAddr.addParam('sysparm_apvRole', 'admin');
	getEmailAddr.getXML(chckRoleAvailability);

	// When the response is back from the server
	function chckRoleAvailability(response){
		var rolesFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		if ( rolesFromScriptInclude != true ){
			g_form.clearValue('request_for');
			g_form.addErrorMessage('Not priviege role, Please retry [Request for].');
		}   
	}
}
