sys_script_client.script:
function onLoad() {

	var stateNow = g_form.getValue('state');
	g_form.addInfoMessage('stateNow=' + stateNow);

	//Type appropriate comment here, and begin script below
	if(g_form.isNewRecord()){
		g_form.setValue('ask_user', g_user.userID);
		//Reset state
		g_form.clearOptions('state');
		g_form.addOption('state','','--None--');
		g_form.addOption('state','submmited','Submmited');
		g_form.setValue('state', stateNow);
	}else{
		var ask_user = g_form.getValue('ask_user');
		var apv_user = g_form.getValue('request_for');
		if( apv_user == g_user.userID ){
			g_form.setReadOnly('request_for', true);
			g_form.setReadOnly('time_limit', true);
			g_form.setReadOnly('shopping', true);

			//set state to disply only Aprroved and Rejected.???
			if(stateNow == ''){
				g_form.setReadOnly('state', true);
			}else if(stateNow == 'submmited'){
				//Reset state
				g_form.clearOptions('state');
				g_form.addOption('state','submmted','Submmited');
				g_form.addOption('state','approved','Approved');
				g_form.addOption('state','rejected','Rejected');
				g_form.setValue('state', stateNow);
			}else{
				//Reset state
				g_form.clearOptions('state');
				g_form.addOption('state','approved','Approved');
				g_form.addOption('state','rejected','Rejected');
				g_form.setValue('state', stateNow);
			}
		}else if( ask_user == g_user.userID ){
			if(stateNow == 'approved' || stateNow == 'rejected' ){
				g_form.setReadOnly('request_for', true);
				g_form.setReadOnly('time_limit', true);
				g_form.setReadOnly('shopping', true);
				g_form.setReadOnly('state', true);
				g_form.setReadOnly('comment', true);
			}else{
				//Reset state
				g_form.clearOptions('state');
				g_form.addOption('state','','--None--');
				g_form.addOption('state','submmited','Submmited');
				g_form.setValue('state', stateNow);
			}
		}else{
			g_form.setReadOnly('request_for', true);
			g_form.setReadOnly('time_limit', true);
			g_form.setReadOnly('shopping', true);
			g_form.setReadOnly('state', true);
		}
	}
}
