var ClientWayCheckRole = Class.create();
ClientWayCheckRole.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	isApvRole:function(){
		user = this.getParameter('sysparm_userID');
		role = this.getParameter('sysparm_apvRole');
	/*
		gs.info('Ask Shoppping receive: ' + JSON.stringify({
            param1: user,
            param2: role
        }));
		gs.addInfoMessage('sysparm_userID:' + user);
		gs.addInfoMessage('sysparm_apvRole:' + role);
	*/
		var shutils = new ShoppingUtils();
		var isAvaila = shutils.hasThisRole(user,role);

	/*
		gs.info('Ask Shoppping return:' + isAvaila );
		gs.addInfoMessage('Ask Shoppping return:' + isAvaila);
	*/
		return isAvaila;
	},

    type: 'ClientWayCheckRole'
});
