sys_script_include.script:
var ShoppingUtils = Class.create();
ShoppingUtils.prototype = {
    initialize: function() {
    },
	hasThisRole: function(userId1, role1){
		var userRecord = new GlideRecord('sys_user');
		userRecord.get(userId1);
		var roles = userRecord.roles + '';
		var list = roles.split(',');
		var i = 0;
		var len = list.length;
		while(i < len){
			if (list[i] == role1 ){
				break;
			}
			i++;
		}
		if (i < len) {
			return true;
		}else{
			return false;
		}
	},

    type: 'ShoppingUtils'
};
